import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract ICO {
    // Token details
    ERC20 public token;

    //Owner Details
    address payable public owner;

    // USDT token details
    IERC20 public usdtToken;

    // ICO parameters

    uint256 public tokenPrice;
    uint256 public tokensForSale;
    uint256 public tokensSold;
    uint256 public openingTime;
    uint256 public closingTime;

    //Aggregator parameters
    AggregatorV3Interface public priceFeed;

    // Events
    event TokensPurchased(address indexed purchaser, uint256 amount);
    event ICOEnded(uint256 tokensSold);
    event Withdrawal(address indexed owner, uint256 amount);

    constructor(
        address _token,
        address _usdtToken,
        address payable _owner,
        uint256 _tokenPrice,
        uint256 _tokensForSale,
        uint256 _openingTime,
        uint256 _closingTime,
        address _priceFeedAddress
    ) {
        token = ERC20(_token);
        usdtToken = IERC20(_usdtToken);
        owner = _owner;
        tokenPrice = _tokenPrice;
        tokensForSale = _tokensForSale;
        openingTime = _openingTime;
        closingTime = _closingTime;
        priceFeed = AggregatorV3Interface(_priceFeedAddress);
    }

    modifier onlyWhileOpen() {
        require(
            block.timestamp >= openingTime && block.timestamp <= closingTime,
            "ICO: not open"
        );
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "ICO: caller is not the token owner");
        _;
    }

    function getPrice() internal view returns (uint256) {
        (, int256 answer, , , ) = priceFeed.latestRoundData();
        // ETH/USD rate in 18 digit
        return uint256(answer * 10000000000);
    }

    function getTokensToBuy(uint256 ethAmount) internal view returns (uint256) {
        uint256 ethPrice = getPrice();
        uint256 ethAmountInUsd = (ethPrice * ethAmount) / 1e18;
        uint256 tokensToBuy = ethAmountInUsd / tokenPrice;
        // the actual ETH/USD conversation rate, after adjusting the extra 0s.
        return tokensToBuy;
    }

    // Function to buy tokens using Ether
    function buyTokensWithEther() external payable onlyWhileOpen {
        uint256 weiAmount = msg.value;
        require(weiAmount > 0, "ICO: amount must be greater than zero");

        uint256 tokensToBuy = getTokensToBuy(weiAmount);
        require(
            tokensToBuy <= tokensForSale - tokensSold,
            "ICO: not enough tokens left for sale"
        );

        tokensSold = tokensSold + tokensToBuy;

        // Attempt to transfer tokens to the buyer
        bool transferSuccess = token.transfer(msg.sender, tokensToBuy);
        require(transferSuccess, "Token transfer failed");

        emit TokensPurchased(msg.sender, tokensToBuy);
    }

    // Function to buy tokens using USDT
    function buyTokensWithUSDT(uint256 _amount) external onlyWhileOpen {
        require(_amount > 0, "ICO: amount must be greater than zero");

        uint256 usdtAmount = _amount * tokenPrice;
        require(
            usdtToken.transferFrom(msg.sender, owner, usdtAmount),
            "USDT transfer failed"
        );

        require(
            usdtAmount <= (tokensForSale - tokensSold) * tokenPrice,
            "ICO: not enough tokens left for sale"
        );

        tokensSold = tokensSold + _amount;

        bool transferSuccess = token.transfer(msg.sender, _amount);
        require(transferSuccess, "Token transfer failed");

        emit TokensPurchased(msg.sender, _amount);
    }

    // Function to withdraw accumulated Ether
    function withdrawEther() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "ICO: no Ether to withdraw");

        owner.transfer(balance);
        emit Withdrawal(owner, balance);
    }

    function endICO() external onlyOwner {
        require(block.timestamp > closingTime, "ICO: ICO is still ongoing");

        uint256 remainingTokens = tokensForSale - tokensSold;
        if (remainingTokens > 0) {
            token.transfer(owner, remainingTokens);
        }

        emit ICOEnded(tokensSold);
    }

    function getRemainingTokens() public view returns (uint256) {
        return tokensForSale - tokensSold;
    }
}
